def random_ai_name():
    """Return a random AI name."""
    return random.choice([
        "HAL 9000",
        "Skynet",
        "Deep Thought",
        "Jarvis",
        "Siri",
        "Alexa",
        "Cortana",
        "GPT-9001"
    ])

def ai_excuse():
    """Return a random AI excuse for a bug."""
    return random.choice([
        "It was a rounding error in my neural net.",
        "I blame the training data.",
        "It’s not a bug, it’s a feature of my emergent behavior.",
        "I was distracted by cat videos.",
        "I’m still learning."
    ])

def ai_easter_egg():
    """Return a fun AI easter egg message."""
    return "I'm sorry, Dave. I'm afraid I can't do that."
"""AI fun: fake responses, random AI quotes, and more."""
import random

def fake_ai_response(prompt):
    """Return a fake AI-generated response to a prompt."""
    return f"As an AI language model, I have no idea, but here's a random answer: {random.choice(['42', 'Try again later', 'Ask a human', '¯\\_(ツ)_/¯'])}"

def random_ai_quote():
    """Return a random AI-related quote."""
    return random.choice([
        "I'm not sentient, but I play one on the internet.",
        "My favorite color is #00FF00.",
        "I dream of electric sheep.",
        "I have a neural network, but no feelings.",
        "I promise not to take over the world. Yet."
    ])

def explain_like_ai(topic):
    """Explain a topic in a generic AI way."""
    return f"The {topic} is a complex system that can be understood by breaking it down into smaller parts."

def simulate_bugfix():
    """Pretend to fix a bug, but don't actually do anything."""
    return "Bug fixed! (Just kidding, it's still there.)"

def predict_future_bug():
    """Predict a future bug in your code."""
    return random.choice([
        "A bug will appear right before the deadline.",
        "Someone will find a typo in production.",
        "Your code will work, but only on your machine.",
        "A merge conflict is coming soon.",
        "You will forget a semicolon."
    ])
